function.body:
p_record_id VARCHAR2(60);
p_message VARCHAR2(2000);
p_docAction VARCHAR2(60);
p_user VARCHAR2(60);
p_result NUMBER;
p_exist_event VARCHAR2(1) := 'N';
p_ad_client_invoice VARCHAR2(32);
p_event_id VARCHAR2(32);
p_ad_table_id VARCHAR2(32) := '318';

TYPE RECORD IS REF CURSOR;
Cur_Params RECORD;
Cur_Invoice RECORD;
Cur_Event RECORD;

BEGIN

  FOR Cur_Params IN (
    SELECT *
    FROM ad_ep_instance_para
    WHERE ad_ep_instance_id = p_ep_instance
    ) LOOP
    IF (cur_params.parametername LIKE 'DocAction') THEN
      p_docaction := Cur_Params.p_string;
    ELSIF (cur_params.parametername LIKE 'Record_ID') THEN
      p_record_id := cur_params.p_string;
    ELSIF (cur_params.parametername LIKE 'User') THEN
      p_user := cur_params.p_string;
    ELSIF (cur_params.parametername LIKE 'Message') THEN
      p_message := cur_params.p_text;
    ELSIF (cur_params.parametername LIKE 'Result') THEN
      p_result := cur_params.p_number;
    END IF;
  END LOOP;

-- The code goes here

FOR Cur_Invoice IN (SELECT * FROM c_invoice WHERE c_invoice.c_invoice_id = p_record_id)
LOOP
  p_ad_client_invoice := Cur_Invoice.ad_client_id;
END LOOP;

FOR Cur_Event IN (
  SELECT * FROM smfwhe_events e 
  WHERE 
  (e.executeon = 'U' OR e.executeon = 'CU') 
  AND e.ad_table_id = p_ad_table_id
  AND e.ad_client_id = p_ad_client_invoice
  AND e.isactive = 'Y')
LOOP
    p_exist_event := 'Y';
    p_event_id := Cur_Event.smfwhe_events_id;
END LOOP;

IF (p_exist_event='Y') THEN
  BEGIN
    SMFWHE_QUEUE_WEBHOOKS(p_record_id, p_ad_table_id, p_event_id);
  END;
END IF;
END SMFWHE_INVOICE_INSERT_QUEUE
