function.body:
p_record_id VARCHAR2(60);
p_ad_table_id VARCHAR2(32);
p_docstatus VARCHAR2(60);

TYPE RECORD IS REF CURSOR;
Cur_Params RECORD;
Cur_Invoice RECORD;

BEGIN

  FOR Cur_Params IN (
    SELECT *
    FROM ad_ep_instance_para
    WHERE ad_ep_instance_id = p_ep_instance
    ) LOOP
    IF (cur_params.parametername LIKE 'Record_ID') THEN
      p_record_id := Cur_Params.p_string;
    ELSIF (cur_params.parametername LIKE 'Table_ID') THEN
      p_ad_table_id := cur_params.p_string;
    END IF;
  END LOOP;

-- The code goes here
IF (p_ad_table_id='318') THEN
  FOR Cur_Invoice IN (SELECT * FROM c_invoice e WHERE e.c_invoice_id=p_record_id)
  LOOP
    p_docstatus := Cur_Invoice.docstatus;
  END LOOP;
  IF (p_docstatus is not null) THEN
    IF (p_docstatus <> 'CO') THEN
      RAISE_APPLICATION_ERROR(-20000, 'The docaction from invoice is not completed');
    END IF;
  END IF;
END IF;
END SMFWHE_TEST_PL
